-- Database: mutant-- DROP DATABASE mutant;CREATE DATABASE mutant  WITH OWNER = postgres       ENCODING = 'UTF8'       TABLESPACE = pg_default       LC_COLLATE = 'Spanish_Argentina.1252'       LC_CTYPE = 'Spanish_Argentina.1252'       CONNECTION LIMIT = -1;-- Sequence: id_seq-- DROP SEQUENCE id_seq;CREATE SEQUENCE id_seq  INCREMENT 1  MINVALUE 1  MAXVALUE 9223372036854775807  START 1  CACHE 1;ALTER TABLE id_seq  OWNER TO postgres;-- Table: dna-- DROP TABLE dna;CREATE TABLE dna(  id bigint NOT NULL,  dna bytea,  is_mutant boolean,  n integer,  CONSTRAINT dna_pkey PRIMARY KEY (id))WITH (  OIDS=FALSE);ALTER TABLE dna  OWNER TO postgres;